import React, { useEffect, useState } from "react";
import {useForm} from "./useForm";
import { Hello } from "./Hello"

// the below function is called when we have a heavy computational task, and we want to only called the func only once
// when component renders for the 1st time // It runs on the intial value
// function expInState () {
//   return 10
// }

const App = () => {
  // const [{count, count2 }, setCount] = useState({ count: 10, count2: 20})
  const [values, handleChange] = useForm({email: '', password: '', firstname: ''});
  // const [values2, handleChange2] = useForm({firstname: '', lastname: ''})

    const [showHello, setShowHello] = useState(true)
    const [pos, setPos] = useState(0)
    // every time this component re-renders it runs the useEffect function
    useEffect(() => {
        const onMouseMove = e => {
         console.log(e)
            setPos(e)
        }
        window.addEventListener('mousemove', onMouseMove)
        // clean up function
        return () => {
            window.removeEventListener('mousemove', onMouseMove)
        }
    }, [values.email])

  return (
    <div>
        <>
            {/*<button onClick={() => setShowHello(!showHello)}>Toggle</button>*/}
            {/*{ showHello && <Hello />}*/}
            Position: { pos }
        <input type="text" name="firstname"
               value={values.firstname}
               onChange={handleChange}
               placeholder="First Name"
        />
        <input type="text" name="email"
               value={values.email}
               onChange={handleChange}
               placeholder="Email"
        />
        <input type="password" name="password"
               value={values.password}
               onChange={handleChange}
               placeholder="Password"
        />
        </>
    </div>
  );
}

export default App;
